import React, { useCallback,useEffect, useState, createRef } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import {Button} from 'antd'

import 
    {
     MAINPOSTS_1001_COMMENTINSERT_REQUEST,
     MAINPOSTS_1001_COMMENTS_REQUEST
    } 
from '../../reducers/mainPosts_1001'; 

const  CommentTextArea= ({postFlag,nickName,postId,userInfo,comment,onChageComment,insertComment}) =>{

    const dispatch = useDispatch(); 
    const ref = createRef(); 
 
    const blank_pattern = /^\s+|\s+&/g;  

    



return (
    <>
    <textarea  value={comment}  style={{width:"100%",height:"80px",marginTop:"3%"}} onChange={(e)=>onChageComment} placeholder={ userInfo ? "댓글을 작성해 보세요!" : "로그인이 필요한 서비스 입니다."} readOnly={userInfo ? false : true}/>
   
    <div style={{margin:"1%",display:"block",float:"left"}}>
      {comment ? comment.length  : 0 } &#47; 300
    </div>

    {userInfo && 
        <div style={{margin:"1%",display:"block",float:"right"}}>
            <Button type="primary" onClick={()=>insertComment}>댓글달기</Button>
        </div>
    }

    </>
)

}

export default CommentTextArea;
