import React, { useCallback,useEffect, useState, createRef, memo } from 'react'



import InputBoxChilde from '../components/testComponent/inputBoxChilde'


//부모의 버튼을 클릭 시 자식의 함수를 실행시키게 하기 
//도대체 어떻게 해야할까?
const InputBoxParent =()=>{

    const [initValue, setInitValue]= useState(''); 
    

    const init = () =>{

        setInitValue('clicked'); 
    }
    return (
        <div>
            <InputBoxChilde clickedValues={initValue}/> 
            <button onClick={init}>클릭!</button>
        </div>
        )

}

export default InputBoxParent;
