import React, { useState } from "react";
import Item from '../components/testComponent/Item'


const Test = () =>{

    const [numbers, setNumbers] = useState([1, 2, 3]);
    const [array, setArray] = useState([]); 


    const addNumber = () => {
      setArray([...numbers]); 
        //배열 추가 시 
        setNumbers(prev => [...prev, prev.length + 1]);

      };

    const updateArray = () =>{

      setArray([...numbers]); 

      numbers.map((v)=>{
        if(v===1){
          array[i] = v = v+1; 
        }
      })

      setNumbers(prev => [...numbers,prev]);

    }

    return (
      <div>
        {numbers.map(num => (
          <Item key={num} num={num}></Item>
        ))}
        <div onClick={addNumber}>Click</div>
        <div onClick={updateArray}>UPDATE</div>
        <input type="text" value={array.length} />
      </div>
    )

}

export default Test;
