import {DislikeTwoTone,LikeTwoTone} from '@ant-design/icons'
import 
    {
     MAINPOSTS_1001_COMMENTS_REQUEST, 
     MAINPOSTS_1001_COMMENTLIKE_REQUEST,
    } 
from '../../reducers/mainPosts_1001'; 

const Comments1001 = ({likeBtn, 
                      createdDate,
                      comment,
                      commentId,
                      flag,
                      good,
                      bad,
                      nickName,
                      who,
                      postFlag,
                      postId,


                    })=>{

  

  const dispatch = useDispatch();

    const [test,setTest] = useState(likeClick); 
    const [goodCount,setGoodCount] = useState(good);  

    

  const [like , setLike]  = useState(false); 
  const [disLike , setDisLike]  = useState(false); 


    let badCount  =parseInt(bad); 

    const {userInfo}        = useSelector((state)=>state.auth);
