      //       postFlag,
      //       who:userInfo, 
      //     }
         
      // }); 
       alert(`${likeDislike}`); 
      
      },[]); 
*/
    return (
                <div  className='divTableRow' >
                    <div  className="divTableCell">
                    <b>{who}</b> &nbsp; <small>{custumDateFormat(createdDate)}</small><br/>
                    {comment}<br />
                    답글 [9]
               
                        <div  style={{marginTop:"1%",display:"block",float:"right"}}>
                            <LikeTwoTone onClick={()=>likeBtn(commentId,flag,'good')} twoToneColor={test ? "#ff0000" : "#1ba640"}/>{good}&nbsp;&nbsp;&nbsp;&nbsp;<DislikeTwoTone onClick={()=>likeBtn(commentId,flag,'bad')} twoToneColor={disLikeClick ? "#ff0000" : "#1ba640"} />{bad}&nbsp;&nbsp;&nbsp;&nbsp;<br />
                        </div>
                    </div>
                </div>
    )

}

export default Comments1001;
