import React, { useCallback } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import 
    {EMP_LIST_REQUEST,} 
from '../reducers/emp'; 


const Emp = () =>{


    const dispatch = useDispatch(); 
    const {emplist}    = useSelector(state => state.emp); 
                                 //store의 state를 불러오는 hook 
                                 //store의 state 중에서 count의 state를 불러온다.

    const getEmplist = useCallback(()=>{
            dispatch({
                type:EMP_LIST_REQUEST, 
                data:{name:'이병헌',job:'부장'}, 
            })
    },[])

                                 
                                 
    return (
        <>
        <button onClick={getEmplist}>EMPLIST</button>
         <div className='divTable' style={{marginTop:'3%'}}>
           <div className='divTableBody'>
            {emplist.map((v,i)=>(
                <div className='divTableRow'>
                     <div className='divTableRow'>{v.EMPNO}</div>
                     <div className='divTableRow'>{v.ENAME}</div>
                </div>
            ))}
            </div>
         </div>
        
        </>
    )

}

export default Emp;
