import React,{useState,useCallback,useEffect} from 'react'
import { number } from 'prop-types';
import Link from 'next/link'

const Pagenation = ({pagenate,dataLength,postsPerPage,nowPage,groupPage}) =>{

    const pageNumers = [];
    const prevPage = nowPage-1; 


        if(nowPage ===0){

            pageNumers.length = 0; 
            for(let i =0; i<groupPage; i++){
            pageNumers.push(i); 
            }

        }

  

        if((nowPage % groupPage ===1) && (nowPage !== 1)){
            pageNumers.length = 0; 
            for(let i=nowPage-1; i<(nowPage-1)+groupPage; i++ ){
            pageNumers.push(i); 
            }
        }


    return(
        <>  
        <nav>
            <ul>
                {nowPage}
                <li style={{display: nowPage === 1 ? "none" : ""}}><Link href={'#'}><a onClick={()=>pagenate(nowPage-1)}>◀</a></Link></li>
                {pageNumers.map((v,i)=>(
                    <li key={v}>
                         <li><Link href={'#'}><a onClick={()=>pagenate(v+1)}>{v+1}</a></Link></li>
                    </li>
                ))}
                <li><Link href={'#'}><a onClick={()=>pagenate(nowPage+1)}>▶</a></Link></li>
            </ul>               
        </nav>
        </>
    )

}

export default Pagenation;
