    }else if(((nowPage % groupPage ===1)) && (nowPage !== 1) ){
            console.log('앞으로 그룹 이동')
            pageNumers.length = 0; 
            for(let i=nowPage-1; i<(nowPage-1)+groupPage; i++ ){
            pageNumers.push(i); 
            }

