             pageNumers.length = 0; 
             for(let i=0; i<groupPageArray.length; i++){
                pageNumers.push(groupPageArray[i]-groupPage); 
             }

    }else{
           console.log('번호 클릭'); 
           pageNumers.length=0; 
           for(let i=0; i< groupPageArray.length; i++){
               pageNumers.push(groupPageArray[i]); 
           }

    }

    //마지막 페이지 처리
    if(dataLength === 0 || dataLength < postsPerPage){
        console.log('마지막 페이지 처리' , pageNumers); 
        let values = [...pageNumers]; 
        pageNumers.length=0; 
        values.map((v,i)=>{
            if(v < nowPage){
                pageNumers.push(values[i]); 
            }
        })
    
    }

    return(
        <>  
