import React,{useState,useCallback,useEffect} from 'react'
import { number } from 'prop-types';
import Link from 'next/link'

const Pagenation = ({pagenate,dataLength,postsPerPage,nowPage}) =>{

    const pageNumers = [];
    const [pages, setPages]  = useState(nowPage)
    if(nowPage){
        setPages(nowPage); 
    }

    if(dataLength !== 2){
        pageNumers.length=0; 
        for(let i=1; i<=postsPerPage; i++){
            pageNumers.push(i);
        }

    }else{
        pageNumers.length=0; 
        for(let i=1; i<=5; i++){
            pageNumers.push(i);
        }
    }


    return(
        <>  
        <nav>
            <ul>
                {pages}
                {pages==!1 ?  <li>◀</li>: ""}
                {pageNumers.map(v=>(
                    <li key={v}>
                         <li><Link href={'#'}><a onClick={()=>pagenate(v)}>{v}</a></Link></li>
                    </li>
                ))}
                <li>▶</li>
            </ul>
                
        </nav>
        </>
    )

}

export default Pagenation;
