import React, { useCallback ,createRef} from 'react'
import { useDispatch, useSelector } from 'react-redux';
import {COUNT_PLUS_REQUEST,countMinusActionm,COUNT_MINUS_REQUEST}from '../reducers/count';


const Count = () =>{


    const dispatch = useDispatch(); 
    const count    = useSelector(state => state.count); 
                                 //store의 state를 불러오는 hook 
                                 //store의 state 중에서 count의 state를 불러온다.

    const toggle = useCallback(param=>()=>{
        
        if(param ==='plus'){
           return  dispatch({type:COUNT_PLUS_REQUEST}); 
        }else if(param ==='minus'){
            return dispatch({type:COUNT_MINUS_REQUEST});  
        }else{
            return; 
        }
    },[]); 



    const ref = createRef(); 
    const testBtn = (e)=>{
       // console.log(e.target.value); 
        console.log( ref.current.getElementsByName('a01').value); 
      
    }
                                 
                              
    return (
        <div>
            <input type="text" ref={ref} name={"a01"} value={"text01"}/><input type="button" value="버튼1" onClick={testBtn}/><br/>
            <input type="text" ref={ref} name={"a02"} value={"text02"}/><input type="button" value="버튼2" onClick={testBtn}/><br/>
            <input type="text" ref={ref} name={"a03"} value={"text03"}/><input type="button" value="버튼3" onClick={testBtn}/><br/>
            <input type="text" ref={ref} name={"a04"} value={"text04"}/><input type="button" value="버튼4" onClick={testBtn}/><br/>
        </div>
 


















   
    )

}

export default Count;
