import React, { useCallback,useEffect, useState, createRef } from 'react'
import { useDispatch, useSelector } from 'react-redux';

import custumDateFormat from  '../../utilComponent/custumDateFormat';
import {DislikeTwoTone,LikeTwoTone} from '@ant-design/icons'
import 
    {
     MAINPOSTS_1001_COMMENTS_REQUEST, 
     MAINPOSTS_1001_COMMENTLIKE_REQUEST,
    } 
from '../../reducers/mainPosts_1001'; 

const Comments1001 = ({createdDate,comment,userNickName,commentId,flag,good,bad}) =>{

    
    const [like , setLike]  = useState(false); 
    const {userInfo}        = useSelector((state)=>state.auth);


    const likeBtn =useCallback((commentid,flag,likeDislike)=>{
        if(!userInfo){
          alert('로그인이 필요한 서비스 입니다.'); 
          return;
        }
      
        //alert(`${commentid},${flag},${likeDislike}`)
        //문자 리터럴로 해야 alert에 변수 들어가진다.
    
        if(flag === "Y"){
          alert('이미 좋아요! 싫어요! 했습니다.'); 
          return; 
    
        }else{
          setLike(true); 
          //댓글 좋아요!
          dispatch({
            type:MAINPOSTS_1001_COMMENTLIKE_REQUEST, 
            data:{
              commentid,
              postFlag,
              postId,
              flag: likeDislike ,
              who: userInfo,
              nickName,
            }
        }); 
    
        }
    
        //댓글 리스트 
        dispatch({
          type:MAINPOSTS_1001_COMMENTS_REQUEST, 
          data:{
            postId,
            nickName,
            postFlag,
            who:userInfo, 
          }
      }); 
       alert(`${likeDislike}`); 
      
      },[]); 

    return (
                <div className='divTableRow' >
                    <div className="divTableCell">
                    <b>{userNickName}</b> &nbsp; <small>{custumDateFormat(createdDate)}</small><br/>
                    {comment}<br />
                    답글 [9]
               
                        <div style={{marginTop:"1%",display:"block",float:"right"}}>{/*#1ba640*/}
                            <LikeTwoTone onClick={()=>likeBtn(commentId,flag,'good')} twoToneColor={like ? "#ff0000" : "#1ba640"}/>{good}&nbsp;&nbsp;&nbsp;&nbsp;<DislikeTwoTone onClick={()=>likeBtn(commentId,flag,'good')} twoToneColor={like ? "#ff0000" : "#1ba640"} />{bad}&nbsp;&nbsp;&nbsp;&nbsp;<br />
                        </div>
                    </div>
                </div>
    )

}

export default Comments1001;
