    //   })

    //   setArray(prev => [...array]);

    // }

    return (
      <div>
        {numbers.map(num => (
          <Item key={num} num={num}></Item>
        ))}
        <div onClick={addNumber}>Click</div>
        <input type="text" value={array.length} />
      </div>
    )

}

export default Test;
