import React, { memo,useCallback,useEffect, useState, createRef } from 'react'


const CreateUser =({ username, email, onChange, onCreate ,array})=>{
 
  console.log('CreateUser=>',array);  
  const [array001,setArray001] = useState([]); 
  useEffect(()=>{
    setArray001([...array]); 
  },[array])

    return (
        <div>
          <input
            name="username"
            placeholder="계정명"
            onChange={onChange}
            value={username}
          />
          <input
            name="email"
            placeholder="이메일"
            onChange={onChange}
            value={email}
          />
          <button onClick={onCreate}>등록</button>
          {array001 && array001.map((v,i)=>(
            <div key={i}>{i}</div>
          ))}
        </div>
      );
}


export default memo(CreateUser);
