import React, { useCallback,useEffect, useState, createRef } from 'react'
import { useDispatch, useSelector } from 'react-redux';

import custumDateFormat from  '../../utilComponent/custumDateFormat';
import {DislikeTwoTone,LikeTwoTone} from '@ant-design/icons'
import 
    {
     MAINPOSTS_1001_COMMENTS_REQUEST, 
     MAINPOSTS_1001_COMMENTLIKE_REQUEST,
    } 
from '../../reducers/mainPosts_1001'; 

const Comments1001 = ({
                      key,
                      postFlag,
                      nickName,
                      postId,
                      userInfo,

                      commentId,
                      comment,
                      who,
                      flag,
                      good,
                      bad,
                      createdDate,
                      isAddcomment,
                      likeDisLikeFalg,
                      hello, 


                      realFlag

                    })=>{

    const dispatch = useDispatch();       
    const {clickCommentId,
           likeIsClicked } =useSelector((state)=>state.mainPosts_1001); 

    const [goodCount,setGoodCount] = useState(parseInt(good));  
    const [badCount,setBadCount]   = useState(parseInt(bad)); 
    const [like , setLike]        = useState(false); 
    const [disLike , setDisLike]  = useState(false); 

    console.log('realFlag=>' , realFlag,'good=>   ' , good,'bad=>  ',bad , '   clickCommentId=> ' , hello , '  commentId=>  ' , commentId, 'flag=>  ' , flag,'  likeIsClicked=>',likeIsClicked,'isAddcomment =>' , isAddcomment,'   likeDisLikeFalg=> ',likeDisLikeFalg); 

    const likeBtn =useCallback((commentid,flag,likeDislike)=>{
        if(!userInfo){
          alert('로그인이 필요한 서비스 입니다.'); 
          return;
        }
          //alert(`${commentid},${flag},${likeDislike}`)
        //문자 리터럴로 해야 alert에 변수 들어가진다.
    
        if(flag==="Y" || likeDisLikeFalg){
          
          alert('이미 좋아요! 싫어요! 했습니다.'); 
          return; 
    
        }else{

            // if(likeDisLikeFalg ==="good"){
            //     setLike(true); 
            //     setDisLike(false); 

            //     console.log('good 버튼 누름commentId==>   ',commentId ,'   goodCount=>' , goodCount, ' good=>  ',good , '  flag=>' , flag); 
            // }else{
            //     setLike(false); 
            //     setDisLike(true); 

            //     console.log('bad 버튼 누름commentId==>   ',commentId ,'   badCount=>' , badCount, ' bad=>  ',bad ); 
            //   }
            alert('goodCount=>' , goodCount); 
            setGoodCount(parseInt(good)+1); 
            alert('goodCount=>' , goodCount); 

        //댓글 좋아요!
          dispatch({
            type:MAINPOSTS_1001_COMMENTLIKE_REQUEST, 
            data:{
              commentid,
              postFlag,
              postId,
              flag: likeDislike ,
              who: userInfo,
              nickName,
            }
        }); 

        // //댓글 가져오기
        // dispatch({
        //     type:MAINPOSTS_1001_COMMENTS_REQUEST, 
        //     data:{
        //       postId,
        //       nickName,
        //       postFlag,
        //       who:userInfo,
        //     }
        // }); 


    
        }

       alert(`${likeDislike}`); 
      
      },[]); 

    return (
                <div  className='divTableRow' >
                    <div  className="divTableCell">
                    <b>{who}</b> &nbsp; <small>{custumDateFormat(createdDate)}</small><br/>
                    {comment}<br />
                    답글 [9] {commentId}
               
                        <div  style={{marginTop:"1%",display:"block",float:"right"}}>
                            <LikeTwoTone onClick={()=>likeBtn(commentId,flag,'good')} twoToneColor={realFlag !== flag && likeDisLikeFalg ==='good' ? "#ff0000" : "#1ba640"}/>{goodCount}
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <DislikeTwoTone onClick={()=>likeBtn(commentId,flag,'bad')} twoToneColor={commentId===hello && likeDisLikeFalg ==='bad' ? "#ff0000" : "#1ba640"} />{bad}
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <br />
                        </div>
                    </div>
                </div>


                
    )

}

export default Comments1001;
