import React, { useCallback,useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';

import 
    {MAINPOSTS_1001_DETAIL_INFO_REQUEST,} 
from '../reducers/mainPosts_1001'; 



const detailPage  = ({nickName,postFlag,postId}) =>{

  const dispatch = useDispatch(); 

  const {mainPosts_1001Info} = useSelector((state)=>state.mainPosts_1001); 

   useEffect(()=>{
  
    
    dispatch({
          type:MAINPOSTS_1001_DETAIL_INFO_REQUEST, 
          data:{
            postId,
            nickName,
            postFlag,
        
          }
    })
  

  
  },[]); 


  const test = () =>{
    return {__html:`<div style="background:#52D4FF;width: 100px;height: 100px;margin:auto;padding:10px; border: 3px solid #73AD21;">a</div>`};
  } 


  const aaa = ()=>{

    alert(mainPosts_1001Info[0].content)
  }

    return (
<>
<div className='divTable' style={{marginTop:'3%'}}>
  <div className='divTableBody'>
    <div className='divTableRowTh'>
        <div className='divTableCellTh'>{mainPosts_1001Info[0].title}</div>
        <div className='divTableCellTh'>작성일:{mainPosts_1001Info[0].createdDate}</div>
  </div>
</div>
      </div>
     <div style={{display:"flex",justifyContent:"center",alignItems:"center",width:"100%",marginTop:"1%"}}>

        <div dangerouslySetInnerHTML={{__html:mainPosts_1001Info[0].content}} style={{}} />
    </div>         
    </>
        )
}


detailPage.getInitialProps = async (context) =>{

  const postId   = context.query.postId; 
  const nickName = context.query.nickName; 
  const postFlag = context.query.postFlag; 
  return {nickName,postFlag,postId}; 
}
export default detailPage;
