import React, {useState, useMemo, useCallback,useRef ,current} from 'react'




const Test2 = () =>{
    const [count, setCount] = useState(0)
    const handleClick = () => console.log('clicked!')
    //컴포넌트가 렌더링 될 때마다 함수를 새로 생성한다는 단점이 있다. 
    //부모 컴포넌트가 렌더링되거나, 상태가 변경되는 경우, React 컴포넌트는 렌더링을 유발한다.
    
    const plus =(a) =>{


        setCount(count+1); 
    }
    
    return (

        <>
        <button onClick={() => plus(1)}>카운트 올리기</button>
        <button onClick={handleClick}>클릭해보세요!</button>
        </>
    )

}


export default Test2;
