import React,{useState,useCallback,useEffect} from 'react'
import { number } from 'prop-types';
import Link from 'next/link'

const Pagenation = ({pagenate,dataLength,postsPerPage,nowPage}) =>{

    const pageNumers = [];
    const prevPage = nowPage-1; 

    if(dataLength !== 2){
        console.log('postsPerPage=> ',postsPerPage)
        pageNumers.length=0; 
        for(let i=1; i<=postsPerPage; i++){
            pageNumers.push(i);
        }

    }else{

        if(nowPage % 5 === 0){
                    console.log(nowPage % 5); 
                console.log('힉힉?'); 
        }
        pageNumers.length=0; 
        for(let i=1; i<=5; i++){
            pageNumers.push(i);
        }
    }


    return(
        <>  
        <nav>
            <ul>
                {nowPage}
                <li style={{display: nowPage === 1 ? "none" : ""}}><Link href={'#'}><a onClick={()=>pagenate(nowPage-1)}>◀</a></Link></li>
                {pageNumers.map((v,i)=>(
                    <li key={v}>
                         <li><Link href={'#'}><a onClick={()=>pagenate(v)}>{v}</a></Link></li>
                    </li>
                ))}
                <li><Link href={'#'}><a onClick={()=>pagenate(nowPage+1)}>▶</a></Link></li>
            </ul>               
        </nav>
        </>
    )

}

export default Pagenation;
