import custumDateFormat from  '../../utilComponent/custumDateFormat';
import {DislikeTwoTone,LikeTwoTone , UserOutlined, FieldTimeOutlined} from '@ant-design/icons'

const Comment1001ByComments = ({
                                key,
                                postFlag,
                                nickName,
                                postId,
                                userInfo,
                                commentId,

                                byCommentId,
                                comment,
                                who,

                                good,
                                bad,
                                flag,
                                createdDate,
                                commentByCommentLikeBtn,
                                })=>{
    return (
        <div className='divTableRow' >
            <div className="divTableCell02">
                    â””&nbsp;<b>{who}</b> &nbsp; <small>{custumDateFormat(createdDate)}</small><br />
                    &nbsp;&nbsp;&nbsp;&nbsp;{comment}
                <div  style={{marginTop:"1%",display:"block",float:"right"}}>
                    <LikeTwoTone onClick={()=>commentByCommentLikeBtn(byCommentId,flag,'good')} twoToneColor={"#1ba640"}/>{good}
                    &nbsp;&nbsp;&nbsp;
                    <DislikeTwoTone onClick={()=>commentByCommentLikeBtn()} twoToneColor={"#1ba640"} />{bad}
                    &nbsp;&nbsp;&nbsp;
                    <br />
                </div> 
            </div>
        </div>   
    )
}

export default memo(Comment1001ByComments);
