import React , {useState,useEffect,useCallback}from 'react'
import styled from 'styled-components'; 
import { useRouter } from 'next/router';
import Link from 'next/link'
import Pagenation from '../utilComponent/Pagenation'
import {DislikeTwoTone,LikeTwoTone} from '@ant-design/icons'
import 
    {MAINPOSTS_1001_LIST_REQUEST,} 
from '../reducers/mainPosts_1001'; 


import 
    {LOAD_USER_REQUEST,
    } 
from '../reducers/auth'; 
import { useDispatch, useSelector } from 'react-redux';
import isEmpty from '../utilComponent/isEmpty';




const mainPosts_1001 = ()=>{

  const dispatch = useDispatch(); 
  const {mainPosts_1001} = useSelector((state)=>state.mainPosts_1001); 


  useEffect(()=>{
    dispatch({
        type:MAINPOSTS_1001_LIST_REQUEST, 
        data:{postFlag:'1001',
              currentPage:nowPage,
              maxPage:postsPerPage
             }, 
    })

    // dispatch({
    //     type:LOAD_USER_REQUEST,
    // }); 
    console.log('mainPosts_1001=>' , mainPosts_1001); 

  },[]); 


  const gotoDetail = useCallback((postId,userNickName,postFlag)=>{

      console.log(postId,userNickName,postFlag);

  },[])

/*-------------------------------------------페이징 처리 로직 start-------------------------------------------------------*/
  const [nowPage,setNowPage] = useState(0);                       //현재 페이지
  const [postsPerPage] = useState(5);                             //한 페이지당 list 수 
  const [groupPage , setGroupPage] = useState(5);                 //페이징 그룹 당 수  1~5 , 6~10 , 11~15 ...
  const [nowGroupPageArray,setNowGroupPageArray] =useState([]);   //현제 페이징 그룹 배열

  const pagenate =useCallback((pageNumber, groupPageArray)=>{
    setNowPage(pageNumber); 
    nowGroupPageArray.length=0; 
    setNowGroupPageArray(nowGroupPageArray.concat(groupPageArray));
    const indexOfLastPost = pageNumber * postsPerPage;   
    const indexOfFirstPost = indexOfLastPost - postsPerPage;  

    dispatch({
      type:MAINPOSTS_1001_LIST_REQUEST, 
      data:{postFlag:'1001',
            currentPage:indexOfFirstPost,
            maxPage:postsPerPage
     }, 

     
  });

},[nowPage,nowGroupPageArray]); 
/*-------------------------------------------페이징 처리 로직   end-------------------------------------------------------*/


   return (
    <>
      <div className="divTable" onClick={gotoDetail(v.postId,v.userNickName,'1001')}>
            {mainPosts_1001.map((v,i)=>(
               <div className='divTableRow'  >
                  <div className='divTableCell'><div className="divImageCell"><img src='https://dimg.donga.com/wps/NEWS/IMAGE/2019/11/24/98510015.1.jpg' /></div></div>
                  <div className="divTableCell" ><Link href={{pathname:'/detailPage' , query:{postId:v.postId,nickName:v.userNickName,postFlag:'1001'}}} as={'detail'}><a>{isEmpty(v.title)}</a></Link><br/>
                  <LikeTwoTone twoToneColor="#1ba640"/> : 10 <DislikeTwoTone twoToneColor="#1ba640"/> : 10 hit : 15,000<br/>
                  {isEmpty(v.createdDate)} | {isEmpty(v.userNickName)}<br />
                  </div>
              </div>
            ))}
      </div>
    

        <Pagenation pagenate={pagenate} dataLength={mainPosts_1001.length} postsPerPage={postsPerPage} nowPage={nowPage} groupPage={groupPage} groupPageArray={nowGroupPageArray} />

    </>
    );
}; 


export default mainPosts_1001;
