import React, { useCallback,useEffect, useState, createRef, memo } from 'react'
import { useDispatch, useSelector } from 'react-redux';

import custumDateFormat from  '../../utilComponent/custumDateFormat';
import {DislikeTwoTone,LikeTwoTone} from '@ant-design/icons'
import 
    {
     MAINPOSTS_1001_COMMENTS_REQUEST, 
     MAINPOSTS_1001_COMMENTLIKE_REQUEST,
    } 
from '../../reducers/mainPosts_1001'; 
import { Badge } from 'antd';

const Comments1001 = ({
                      key,
                      postFlag,
                      nickName,
                      postId,
                      userInfo,

                      commentId,
                      comment,
                      who,
                      flag,
                      
                      good,
                      bad,
                      createdDate,

                      likeBtn,

                      clickedComponent,
                      likeDislikeflag,

                    })=>{
                        useEffect(()=>{
                            console.log('Comments1001 컴포넌트'); 
                        },[])
        
        const [unfoldList,setUnfoldList] = useState(''); 
        const array =[{id:'1001',comment:'아이고',modifierdate:'20201124'},
                      {id:'1002',comment:'helloWorld',modifierdate:'20201124'},
                      {id:'1003',comment:'goodWorld',modifierdate:'20201124'},
                      {id:'1004',comment:'pickWorld',modifierdate:'20201124'},
    ]

        const commentByCommentList =()=>{

            alert('hello world'); 
            setUnfoldList('hello'); 
        }


    return (
        <>
                <div  className='divTableRow' >
                    <div  className="divTableCell">
                    <b>{who}</b> &nbsp; <small>{custumDateFormat(createdDate)}</small><br/>
                    {comment}<br />
                   <a onClick={commentByCommentList}>답글 [9]</a> {commentId}
                   
                        <div  style={{marginTop:"1%",marginLeft:"5%",display:"block",float:"right"}}>
                            <LikeTwoTone onClick={()=>likeBtn(commentId,flag,'good')} twoToneColor={clickedComponent && likeDislikeflag==='good' ? "#ff0000" : "#1ba640"}/>{clickedComponent && likeDislikeflag==='good' ? parseInt(good)+1:good}
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <DislikeTwoTone onClick={()=>likeBtn(commentId,flag,'bad')} twoToneColor={clickedComponent && likeDislikeflag==='bad' ? "#ff0000" : "#1ba640"} />{clickedComponent && likeDislikeflag==='bad' ? parseInt(bad)+1:bad}
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <br />
                        </div> 
                    </div>
                </div>    
                {unfoldList && array.map((v,i)=>(
                    <div className='divTableRow' >
                    <div className="divTableCell">
                    &nbsp;&nbsp;&nbsp;&nbsp;<b>{v.id} <small>{v.modifierdate}</small></b><br />
                    &nbsp;&nbsp;&nbsp;&nbsp;{v.comment}
                    <div  style={{marginTop:"1%",display:"block",float:"right"}}>
                        <LikeTwoTone onClick={()=>likeBtn(commentId,flag,'good')} twoToneColor={clickedComponent && likeDislikeflag==='good' ? "#ff0000" : "#1ba640"}/>{clickedComponent && likeDislikeflag==='good' ? parseInt(good)+1:good}
                        &nbsp;&nbsp;&nbsp;
                        <DislikeTwoTone onClick={()=>likeBtn(commentId,flag,'bad')} twoToneColor={clickedComponent && likeDislikeflag==='bad' ? "#ff0000" : "#1ba640"} />{clickedComponent && likeDislikeflag==='bad' ? parseInt(bad)+1:bad}
                        &nbsp;&nbsp;&nbsp;
                        <br />
                    </div> 
                    </div>
                </div>   
                ))}  
     
        </>   


                
    )

}

export default React.memo(Comments1001);
