import {Editor} from 'react-draft-wysiwyg'
import {EditorState} from 'draft-js'
import { convertFromRaw } from 'draft-js';
import { useState, useCallback } from 'react';



const Myeditor = () =>{

    const [editorState, setEditorState] = useState(() =>
    EditorState.createEmpty(),
    );

    const content = {"entityMap":{},"blocks":[{"key":"637gr","text":"Initialized from content state.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}]};

    const [contentState , setContentState] = useState(convertFromRaw(content));

    const contentChange = useCallback((content)=>{
        console.log(content); 
        setContentState(content); 

    },[]); 


    // onEditorStateChange={editStateChange}
    // const editStateChange = useCallback((editState)=>{
    //     console.log(editState); 
    //     setEditorState(editState); 

    // },[])

    return (    
        
         <Editor
            editState={editorState}
            wrapperClassName="demo-wrapper"
            editorClassName="demo-editor"
           localization={{
               locale:'ko', 
           }}
        
           onContentStateChange={contentChange}
            />

        )
}

export default Myeditor;
