import React, { useCallback,useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';

import 
    {MAINPOSTS_1001_DETAIL_INFO_REQUEST,} 
from '../reducers/mainPosts_1001'; 



const detailPage  = ({nickName,postFlag,postId}) =>{

  const dispatch = useDispatch(); 

  const {mainPosts_1001Info} = useSelector((state)=>state.mainPosts_1001); 

   useEffect(()=>{
  
    console.log('첫 마운트');     
    dispatch({
          type:MAINPOSTS_1001_DETAIL_INFO_REQUEST, 
          data:{
            postId,
            nickName,
            postFlag,
        
          }
    })
  
    console.log('mainPosts_1001Info=>' , mainPosts_1001Info[0]); 
  
  },[mainPosts_1001Info]); 

  const abc = () =>{
    return {__html:`<p>최고예요</p><figure class="image"><img src="http://localhost:3095/pictureTest1600137863784.jpg"></figure>`};
  } 

  const test = () =>{
    return {__html:`<div style="background:#52D4FF;width: 100px;height: 100px;margin:auto;padding:10px; border: 3px solid #73AD21;">a</div>`};
  } 




    return (
      <div>

      {mainPosts_1001Info.map((v,i)=>(
        <p>{v.content}</p>
        ))}
        </div>
        )
}


detailPage.getInitialProps = async (context) =>{

  const postId   = context.query.postId; 
  const nickName = context.query.nickName; 
  const postFlag = context.query.postFlag; 

  console.log('init'); 
  context.store.dispatch({
    type:MAINPOSTS_1001_DETAIL_INFO_REQUEST, 
    data:{
      postId,
      nickName,
      postFlag,
  
    }
}); 


  return {nickName,postFlag,postId}; 
}
export default detailPage;
