          type:MAINPOSTS_1001_DETAIL_INFO_REQUEST, 
          data:{
            postId,
            nickName,
            postFlag,
          }
    });

    //댓글 리스트 
    dispatch({
          type:MAINPOSTS_1001_COMMENTS_REQUEST, 
          data:{
            postId,
            nickName,
            postFlag,
            who:userInfo, 
          }
    }); 
    setMainPosts_1001_commentsArray([...mainPosts_1001Comments]); 


  },[mainPosts_1001Comments.length > 0]); 

  const test = ()=>{

    setMainPosts_1001_commentsArray([...mainPosts_1001Comments]); 
  }

  const likeBtn =useCallback((commentid,flag,likeDislike)=>{
    console.log('mainPosts_1001Comments==>' , mainPosts_1001Comments.length); 
    setMainPosts_1001_commentsArray(prev=>[...prev,mainPosts_1001Comments]); 
    console.log('참나...?=>' , mainPosts_1001_commentsArray); 
    mainPosts_1001Comments.map((v,i)=>{
      if(v.commentId === commentid){
        console.log('폴스야?' ,v); 
        mainPosts_1001_commentsArray[i] = {...mainPosts_1001Comments[i], clickedComponent:`i want hello ${commentid}`}
      }
      console.log('진짜 물음표네??' , mainPosts_1001_commentsArray.length); 
    }); 

    setMainPosts_1001_commentsArray([...mainPosts_1001_commentsArray]); 
    mainPosts_1001_commentsArray.map((v)=>{
      if(v.commentId === commentid){
        console.log('추가됬냐?' , v); 
      }
      console.log('뭔데 왜 하나만 나옴=>' ,  mainPosts_1001_commentsArray.length); 
    })
  
    
        if(!userInfo){
          alert('로그인이 필요한 서비스 입니다.'); 
          return;
        }
          //alert(`${commentid},${flag},${likeDislike}`)
        //문자 리터럴로 해야 alert에 변수 들어가진다.
    
        if(flag==="Y" ){
          
          alert('이미 좋아요! 싫어요! 했습니다.'); 
          return; 
    
        }else{
            //alert('likeDisLikeFalg->' , likeDisLikeFalg); 
            // if(likeDislike === "good"){

            //   setGoodCount(parseInt(good)+1); 

            // }else{

            //   setBadCount(parseInt(bad)+1);

            //   }

     

          //댓글 좋아요!
          dispatch({
            type:MAINPOSTS_1001_COMMENTLIKE_REQUEST, 
            data:{
              commentid,
              postFlag,
              postId,
              flag: likeDislike ,
              who: userInfo,
              nickName,
            }
        }); 


               // 댓글 리스트 
        //   dispatch({
        //     type:MAINPOSTS_1001_COMMENTS_REQUEST, 
        //     data:{
        //       postId,
        //       nickName,
        //       postFlag,
        //       who:userInfo,
        //     }
        // }); 
    
        }

       alert(`${likeDislike}`); 
      
      },[mainPosts_1001Comments.length > 0]); 


     /* 
  const insertComment = useCallback((e)=>{
if(comment.length === 0 || comment.replace(blank_pattern,'')===""){
      ref.current.focus();  
      alert('댓글을 입력해 주세요!'); 
      return; 
    }

    if(comment.length === 300){
      alert('300자 이상 입력 할 수 없습니다.'); 
      return; 
    }
      //댓글 입력
      dispatch({
        type:MAINPOSTS_1001_COMMENTINSERT_REQUEST, 
        data:{
          postId,
          postFlag,
          nickName ,
          who:userInfo, 
          comment,
        }
    });

    setComment(''); 


    //댓글 리스트 
    //   dispatch({
    //     type:MAINPOSTS_1001_COMMENTS_REQUEST, 
