              key={i}
              postFlag={postFlag} 
              nickName={nickName} 
              postId={postId} 
              userInfo={userInfo}
    
              commentId={v.commentId} 
              comment={v.comment} 
              who={v.who} 
              flag={clickedInfo[0] !== null && clickedInfo[0].clickCommentId===v.commentId ? 'Y': v.flag} 
              realFlag={v.flag}
