        //     pageNumers.push(i); 
        //     }

        // }


        if(nowPage===0){

            for(let i=0; i<groupPage; i++){
           
                pageNumers.push(i); 
            }

        }else if(((nowPage % groupPage ===1))  ){

           // pageNumers.length = 0; 
            for(let i=nowPage-1; i<(nowPage-1)+groupPage; i++ ){
            pageNumers.push(i); 
            }

        }else{
            setPageNumers(groupPageArray);
        }

        console.log( 'pageNumers===>' ,   pageNumers); 
        // for(let i=0; i<groupPage; i++){
           
        //     pageNumers.push(i); 

        //     if(((nowPage % groupPage ===1)) && (nowPage !== 1)){
