        //     pageNumers.push(i); 
        //     }
        // }


    return(
        <>  
        <nav>
            <ul>
                {nowPage}
                <li style={{display: nowPage === 1 || nowPage === 0  ? "none" : ""}}><Link href={'#'}><a onClick={()=>pagenate(nowPage-1)}>◀</a></Link></li>
               
               
                {pageNumers.map((v,i)=>(
                    <li key={v}>
                         <li><Link href={'#'}><a onClick={()=>pagenate(v+1,pageNumers)}>{v+1}</a></Link></li>
                    </li>
                ))}



                <li><Link href={'#'}><a onClick={()=>pagenate(nowPage+1)}>▶</a></Link></li>
            </ul>               
        </nav>
        </>
    )

}

export default Pagenation;
