import React, { useCallback,useEffect, useState, createRef ,useRef,useMemo} from 'react'
import CreateUser from '../components/testComponent/CreateUser'
import UserList from '../components/testComponent/UserList'


function countActiveUsers(users) {
    console.log('활성 사용자 수를 세는중...');
    return users.filter(user => user.active).length;
  }
  
  function Test() {
    const [inputs, setInputs] = useState({
      username: '',
      email: ''
    });
    
    const { username, email } = inputs;
    
    const onChange = useCallback(e => {
      const { name, value } = e.target;
      setInputs({
        ...inputs,
        [name]: value
      });
    },[]);
    
    const [users, setUsers] = useState([
      {
        id: 1,
        username: 'DevRappers',
        email: 'devrappers@gmail.com',
        active: true
      },
      {
        id: 2,
        username: 'tester',
        email: 'tester@example.com',
        active: false
      },
      {
        id: 3,
        username: 'liz',
        email: 'liz@example.com',
        active: false
      }
    ]);
  
    const nextId = useRef(4);
    
    const onCreate = useCallback(() => {
      const user = {
        id: nextId.current,
        username,
        email
      };
      //setUsers([...users, user]);
      
      setUsers(users=>users.concat(user));
      
      setInputs({
        username: '',
        email: ''
      });
      nextId.current += 1;
    },[username, email]);
    
    const onRemove = useCallback(id => {
      setUsers(users => users.filter(user => user.id !== id));
    },[]);
    
    const onToggle = useCallback(id => {
      setUsers(users =>
        users.map(user =>
          user.id === id ? { ...user, active: !user.active } : user
        )
      );
    },[]);
    
    const count = useMemo(() => countActiveUsers(users), [users]);
    
    return (
         <div>
        <CreateUser
          username={username}
          email={email}
          onChange={onChange}
          onCreate={onCreate}
        />
        <UserList users={users} onRemove={onRemove} onToggle={onToggle}/>
        <div>활성사용자 수 : {count}</div>
        </div>
    );
  }
  
  export default Test;
