import React, { useCallback,useEffect, useState, createRef } from 'react'
import { useDispatch, useSelector } from 'react-redux';

import custumDateFormat from  '../../utilComponent/custumDateFormat';
import {DislikeTwoTone,LikeTwoTone} from '@ant-design/icons'
import 
    {
     MAINPOSTS_1001_COMMENTS_REQUEST, 
     MAINPOSTS_1001_COMMENTLIKE_REQUEST,
    } 
from '../../reducers/mainPosts_1001'; 

const Comments1001 = ({
                      key,
                      postFlag,
                      nickName,
                      postId,
                      userInfo,

                      commentId,
                      comment,
                      who,
                      flag,
                      good,
                      bad,
                      createdDate,

                      click,
                    })=>{

       
    const [goodCount,setGoodCount] = useState(parseInt(good));  
    const [badCount,setBadCount]   = useState(parseInt(bad)); 
    const [like , setLike]        = useState(false); 
    const [disLike , setDisLike]  = useState(false); 

    const dispatch = useDispatch();    
    
    
   console.log('commentId=>   ',commentId,'  like=> ' , like, 'click=> ',click , 'good=>  ', good, 'bad=>  ' , bad); 




    const likeBtn =useCallback((commentid,flag,likeDislike)=>{
        if(!userInfo){
          alert('로그인이 필요한 서비스 입니다.'); 
          return;
        }
      
        //alert(`${commentid},${flag},${likeDislike}`)
        //문자 리터럴로 해야 alert에 변수 들어가진다.
    
        if(flag === "Y"){
          alert('이미 좋아요! 싫어요! 했습니다.'); 
          return; 
    
        }else{
            if(likeDislike ==="good"){
                setLike(true); 
                setDisLike(false); 

                setGoodCount(0); 
                setGoodCount(parseInt(good)+1); 
                console.log('commentId==>   ',commentId ,'   good count=>' , good, ' good=>  ', goodCount); 
            }else{
                setLike(false); 
                setDisLike(true); 

                setBadCount(0); 
                setBadCount(parseInt(bad)+1)

                console.log('bad count=>' , badCount, ' bad=>  ', bad); 
            }

        //댓글 좋아요!
          dispatch({
            type:MAINPOSTS_1001_COMMENTLIKE_REQUEST, 
            data:{
              commentid,
              postFlag,
              postId,
              flag: likeDislike ,
              who: userInfo,
              nickName,
            }
        }); 

    
        }

       alert(`${likeDislike}`); 
      
      },[like || disLike || key]); 

    return (
                <div  className='divTableRow' >
                    <div  className="divTableCell">
                    <b>{who}</b> &nbsp; <small>{custumDateFormat(createdDate)}</small><br/>
                    {comment}<br />
                    답글 [9] {commentId}
               
                        <div  style={{marginTop:"1%",display:"block",float:"right"}}>
                            <LikeTwoTone onClick={()=>likeBtn(commentId,flag,'good')} twoToneColor={like && click ? "#ff0000" : "#1ba640"}/>{like && click ? goodCount : good}
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <DislikeTwoTone onClick={()=>likeBtn(commentId,flag,'bad')} twoToneColor={disLike && click ? "#ff0000" : "#1ba640"} />{bad}
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <br />
                        </div>
                    </div>
                </div>


                
    )

}

export default Comments1001;
