import React, { useCallback,useEffect, useState, createRef } from 'react'
import { useDispatch, useSelector } from 'react-redux';

import custumDateFormat from  '../../utilComponent/custumDateFormat';
import {DislikeTwoTone,LikeTwoTone} from '@ant-design/icons'
import 
    {
     MAINPOSTS_1001_COMMENTS_REQUEST, 
     MAINPOSTS_1001_COMMENTLIKE_REQUEST,
    } 
from '../../reducers/mainPosts_1001'; 

const Comments1001 = ({
                      key,
                      postFlag,
                      nickName,
                      postId,
                      userInfo,

                      commentId,
                      comment,
                      who,
                      flag,
                      good,
                      bad,
                      createdDate,

                      click,
                    })=>{

    const dispatch = useDispatch();       
    const {clickCommentId,
           likeIsClicked } =useSelector((state)=>state.mainPosts_1001); 
    
    const [aa,setaa] = useState(likeIsClicked); 

    // const [like , setLike]        = useState(false); 
    // const [disLike , setDisLike]  = useState(false); 


    console.log('clickCommentId=> ' , clickCommentId , '  commentId=>  ' , commentId,  'flag=>  ' , flag,'  likeIsClicked=>',likeIsClicked); 

    const likeBtn =useCallback((commentid,flag,likeDislike)=>{
        if(!userInfo){
          alert('로그인이 필요한 서비스 입니다.'); 
          return;
        }
          //alert(`${commentid},${flag},${likeDislike}`)
        //문자 리터럴로 해야 alert에 변수 들어가진다.
    
        if(flag==="Y" || commentId===clickCommentId){
          
          alert('이미 좋아요! 싫어요! 했습니다.'); 
          return; 
    
        }else{

            if(likeDislike ==="good"){
               // setLike(true); 
              //  setDisLike(false); 

                console.log('good 버튼 누름commentId==>   ',commentId ,  ' good=>  ',good , '  flag=>' , flag); 
            }else{
               // setLike(false); 
              //  setDisLike(true); 

                console.log('bad 버튼 누름commentId==>   ',commentId ,  ' bad=>  ',bad ); 
              }

        //댓글 좋아요!
          dispatch({
            type:MAINPOSTS_1001_COMMENTLIKE_REQUEST, 
            data:{
              commentid,
              postFlag,
              postId,
              flag: likeDislike ,
              who: userInfo,
              nickName,
            }
        }); 

    
        }

       alert(`${likeDislike}`); 
      
      },[]); 

    return (
                <div  className='divTableRow' >
                    <div  className="divTableCell">
                    <b>{who}</b> &nbsp; <small>{custumDateFormat(createdDate)}</small><br/>
                    {comment}<br />
                    답글 [9] {commentId}
               
                        <div  style={{marginTop:"1%",display:"block",float:"right"}}>
                            <LikeTwoTone onClick={()=>likeBtn(commentId,flag,'good')} twoToneColor={commentId===clickCommentId ? "#ff0000" : "#1ba640"}/>{click ? parseInt(good)+1 : good}
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <DislikeTwoTone onClick={()=>likeBtn(commentId,flag,'bad')} twoToneColor={commentId===clickCommentId ? "#ff0000" : "#1ba640"} />{click ? parseInt(bad)+1 : bad}
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <br />
                        </div>
                    </div>
                </div>


                
    )

}

export default Comments1001;
