import React, { useCallback,useEffect, useState } from 'react'
import {Button, Input} from 'antd'


const Join = ()=>{


    const [id,setId] = useState(''); 
    const [nickName, setNickName] = useState('');
    const [password, setPassword] = useState('');
    const [passwordCheck, setPasswordCheck] = useState(''); 
    const [email,setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [passwordErr,setPasswordErr] = useState(false); 

    if(password !== passwordCheck){
        return setPasswordErr(true); 
    }

    const onChangeId = (e)=>{
            console.log('setid'); 
            setId(e.target.value); 
    }

    const onChangeNickName = (e)=>{
        setNickName(e.target.value); 
    }

    const onChangPassword =(e)=>{
        setPassword(e.target.value); 
    }

    const onChangePasswordCheck = useCallback((e)=>{
        setPasswordErr(e.target.value !== password); 
        setPasswordCheck(e.target.value); 
    },[password])

    const onChangeEmail =(e)=>{
        setEmail(e.target.value); 
    }

    const onChangePhone =(e)=>{
        setPhone(e.target.value); 

    }

    const onChangeAddress =(e)=>{
        setAddress(e.target.value); 
    }


    return (
            <>
            <form >
                <div className='divTable' style={{marginTop:'3%'}} >
                    <div className='divTableBody'>
                        <div className='divTableRow'>
                                <div className='divTableCell'>아이디:</div>
                                <Input type="text" name="id" onChange={onChangeId}/>
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>닉네임:</div>
                                <Input type="text" name="nickname" onChange={onChangeNickName}/>
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>비밀번호:</div>
                                <Input type="password" name="password" onChange={onChangPassword}/>
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>비밀번호 체크:</div>
                                <Input type="password" name="passwordCheck" onChange={onChangePasswordCheck}/>
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>이메일:</div>
                            <Input type="text" name="email" placeholder="EX) aaa@naver.com" onChange={onChangeEmail}/>
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>전화번호:</div>
                            <Input type="text" name="phone" onChange={onChangePhone}/>
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>주소:</div>
                            <Input type="text" name="adress" onChange={onChangeAddress}/>
                        </div>
                    </div>
                </div>
                <Button>가입하기</Button>
            </form>
            </>
    )


}

export default Join;
