import React, { useCallback,useEffect, useState, createRef } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import {Button} from 'antd'

import 
    {
     MAINPOSTS_1001_COMMENTINSERT_REQUEST,
     MAINPOSTS_1001_COMMENTS_REQUEST
    } 
from '../../reducers/mainPosts_1001'; 

const  CommentTextArea= ({postFlag,nickName,postId,userInfo}) =>{

    const dispatch = useDispatch(); 
    const ref = createRef(); 
    const [comment, setComment] = useState(''); 
    const blank_pattern = /^\s+|\s+&/g;  

    
  //댓글 입력 textArea 
  const onChageComment =useCallback((e)=>{

    if(comment.length === 300){
      alert('300자 이상 입력 할 수 없습니다.'); 
      return; 
    }
    setComment(e.target.value); 

  },[comment]); 
  


  const insertComment = useCallback((e)=>{
    if(comment.length === 0 || comment.replace(blank_pattern,'')===""){
          ref.current.focus();  
          alert('댓글을 입력해 주세요!'); 
          return; 
        }
    
        if(comment.length === 300){
          alert('300자 이상 입력 할 수 없습니다.'); 
          return; 
        }
          //댓글 입력
          dispatch({
            type:MAINPOSTS_1001_COMMENTINSERT_REQUEST, 
            data:{
              postId,
              postFlag,
              nickName ,
              who:userInfo, 
              comment,
            }
        });
    
        setComment(''); 
    
    
       // 댓글 리스트 
        //   dispatch({
        //     type:MAINPOSTS_1001_COMMENTS_REQUEST, 
        //     data:{
        //       postId,
        //       nickName,
        //       postFlag,
        //       who:userInfo,
        //     }
        // }); 
      },[comment]);





return (
    <>
    <textarea  value={comment}  style={{width:"100%",height:"80px",marginTop:"3%"}} onChange={onChageComment} placeholder={ userInfo ? "댓글을 작성해 보세요!" : "로그인이 필요한 서비스 입니다."} readOnly={userInfo ? false : true}/>
   
    <div style={{margin:"1%",display:"block",float:"left"}}>
      {comment ? comment.length  : 0 } &#47; 300
    </div>

    {userInfo && 
        <div style={{margin:"1%",display:"block",float:"right"}}>
            <Button type="primary" onClick={insertComment}>댓글달기</Button>
        </div>
    }

    </>
)

}

export default CommentTextArea;
