  const insertComment = useCallback((e)=>{
if(comment.length === 0 || comment.replace(blank_pattern,'')===""){
      ref.current.focus();  
      alert('댓글을 입력해 주세요!'); 
      return; 
    }

    if(comment.length === 300){
      alert('300자 이상 입력 할 수 없습니다.'); 
      return; 
    }
      //댓글 입력
      dispatch({
        type:MAINPOSTS_1001_COMMENTINSERT_REQUEST, 
        data:{
          postId,
          postFlag,
          nickName ,
          who:userInfo, 
