      </div>
    </div>
    {/*좋아요 싫어요 버튼--------------------------------------------------------------------------------*/}





    {/*댓글 입력--------------------------------------------------------------------------------*/}
   <CommentTextArea   
            postFlag={postFlag} 
            nickName={nickName} 
            postId={postId} 
            userInfo={userInfo}
   />
    {/* 
    <textarea  value={comment} ref={ref} style={{width:"100%",height:"80px",marginTop:"3%"}} onChange={onChageComment} placeholder={ userInfo ? "댓글을 작성해 보세요!" : "로그인이 필요한 서비스 입니다."} readOnly={userInfo ? false : true}/>
   
