import React, { useCallback,useEffect, useState, createRef } from 'react'
import { useDispatch, useSelector } from 'react-redux';

import custumDateFormat from  '../../utilComponent/custumDateFormat';
import {DislikeTwoTone,LikeTwoTone} from '@ant-design/icons'
import 
    {
     MAINPOSTS_1001_COMMENTS_REQUEST, 
     MAINPOSTS_1001_COMMENTLIKE_REQUEST,
    } 
from '../../reducers/mainPosts_1001'; 

const Comments1001 = ({
                      key,
                      postFlag,
                      nickName,
                      postId,
                      userInfo,

                      commentId,
                      comment,
                      who,
                      flag,
                      good,
                      bad,
                      createdDate,

                      click,
                    })=>{

    const dispatch = useDispatch();       
    const {clickCommentId,
           likeIsClicked } =useSelector((state)=>state.mainPosts_1001); 

    const [goodCount,setGoodCount] = useState(parseInt(good));  
    const [badCount,setBadCount]   = useState(parseInt(bad)); 
    const [like , setLike]        = useState(false); 
    const [disLike , setDisLike]  = useState(false); 
    const [valueFlag , setValueFlag] = useState(flag); 
    console.log('valueFlag=>' , valueFlag); 
    const likeBtn =useCallback((commentid,flag,likeDislike)=>{
        if(!userInfo){
          alert('로그인이 필요한 서비스 입니다.'); 
          return;
        }
              //alert(`${commentid},${flag},${likeDislike}`)
        //문자 리터럴로 해야 alert에 변수 들어가진다.
    
        if(valueFlag === "Y" ){
          alert('이미 좋아요! 싫어요! 했습니다.'); 
          return; 
    
        }else{
            if(likeDislike ==="good"){
                setLike(true); 
                setDisLike(false); 
                setValueFlag('Y'); 

                console.log('good 버튼 누름commentId==>   ',commentId ,'   goodCount=>' , goodCount, ' good=>  ',good ); 
            }else{
                setLike(false); 
                setDisLike(true); 
                setValueFlag('Y'); 

                console.log('bad 버튼 누름commentId==>   ',commentId ,'   badCount=>' , badCount, ' bad=>  ',bad ); 
              }

        //댓글 좋아요!
          dispatch({
            type:MAINPOSTS_1001_COMMENTLIKE_REQUEST, 
            data:{
              commentid,
              postFlag,
              postId,
              flag: likeDislike ,
              who: userInfo,
              nickName,
            }
        }); 

    
        }

       alert(`${likeDislike}`); 
      
      },[]); 

    return (
                <div  className='divTableRow' >
                    <div  className="divTableCell">
                    <b>{who}</b> &nbsp; <small>{custumDateFormat(createdDate)}</small><br/>
                    {comment}<br />
                    답글 [9] {commentId}
               
                        <div  style={{marginTop:"1%",display:"block",float:"right"}}>
                            <LikeTwoTone onClick={()=>likeBtn(commentId,flag,'good')} twoToneColor={clickCommentId === commentId && like? "#ff0000" : "#1ba640"}/>{like ? parseInt(good)+1 : good}
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <DislikeTwoTone onClick={()=>likeBtn(commentId,flag,'bad')} twoToneColor={clickCommentId === commentId && disLike? "#ff0000" : "#1ba640"} />{disLike ? parseInt(bad)+1 : bad}
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <br />
                        </div>
                    </div>
                </div>


                
    )

}

export default Comments1001;
