      setNumbers(prev => [...numbers,prev]);

    }

    return (
      <div>
        {numbers.map(num => (
          <Item key={num} num={num}></Item>
        ))}
        <div onClick={addNumber}>Click</div>
        <div onClick={updateArray}>UPDATE</div>
