import React, { useCallback,useEffect, useState } from 'react'
import {Button} from 'antd'


const Join = ()=>{


    const [id,setId] = useState(''); 
    const [nickName, setNickName] = useState('');
    const [password, setPassword] = useState('');
    const [passwordCheck, setPasswordCheck] = useState(''); 
    const [email,setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [passwordErr,setPasswordErr] = useState(false); 

    if(password !== passwordCheck){
        return setPasswordErr(true); 
    }

    const onChangeId = useCallback((e)=>{
            setId(e.target.value); 
    },[id])

    const onChangeNickName = useCallback((e)=>{
        setNickName(e.target.value); 
    },[nickName])

    const onChangPassword = useCallback((e)=>{
        setPassword(e.target.value); 
    },[password])

    const onChangePasswordCheck = useCallback((e)=>{
        setPasswordErr(e.target.value !== password); 
        setPasswordCheck(e.target.value); 
    },[passwordCheck])

    const onChangeEmail = useCallback((e)=>{
        setEmail(e.target.value); 
    },[email])

    const onChangePhone = useCallback((e)=>{
        setPhone(e.target.value); 

    },[phone])

    const onChangeAddress = useCallback((e)=>{
        setAddress(e.target.value); 
    },[address])


    return (
            <>
            <form >
                <div className='divTable' style={{marginTop:'3%'}} >
                    <div className='divTableBody'>
                        <div className='divTableRow'>
                                <div className='divTableCell'>아이디:</div>
                                <input type="text" name="id" onChange={onChangeId}/>
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>닉네임:</div>
                                <input type="text" name="nickname" onChange={onChangeNickName} />
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>비밀번호:</div>
                                <input type="text" name="password" onChange={onChangPassword} />
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>비밀번호 체크:</div>
                                <input type="text" name="passwordCheck" onChange={onChangePasswordCheck}/>
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>이메일:</div>
                            <input type="text" name="email" placeholder="EX) aaa@naver.com" onChange={onChangeEmail}/>
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>전화번호:</div>
                            <input type="text" name="phone" onChange={onChangePhone}/>
                        </div>
                        <div className='divTableRow'>
                                <div className='divTableCell'>주소:</div>
                            <input type="text" name="adress" onChange={onChangeAddress}/>
                        </div>
                    </div>
                </div>
                <Button>가입하기</Button>
            </form>
            </>
    )


}

export default Join;
